import React from 'react'
import { useState } from 'react'

import { classes } from './styles'
import Input from './Input'
import { Container, Avatar, Button, Paper, Grid, Typography, TextField } from '@mui/material'

const Auth = () => {

  const [isSignup, setIsSignup] = useState(false)

  const handleSubmit = () => {

  }

  return (
    <>
    <Container component='main' sx={classes.container} maxWidth='xs'>
      <Paper>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {isSignup && (
            <>
              <Input name='firstName' label='First Name' type='text' autoFocus />
            </>
            )}
            <Input name='email' label='Email' type='email'/>
          </Grid>
        </form>
      </Paper>
      
    </Container>

  </>
  )
}

export default Auth
